// Add this function to handle audio playback
function handlePlayback(audioElement, playButton) {
    const allAudios = document.querySelectorAll('audio');
    const allPlayButtons = document.querySelectorAll('.play-song-btn i');

    // Pause all other audios
    allAudios.forEach(audio => {
        if (audio !== audioElement) {
            audio.pause();
        }
    });

    // Reset all other play buttons
    allPlayButtons.forEach(btn => {
        btn.className = 'fas fa-play';
    });

    if (audioElement.paused) {
        audioElement.play();
        playButton.className = 'fas fa-pause';
    } else {
        audioElement.pause();
        playButton.className = 'fas fa-play';
    }
}

// Update createSongCard to include playback handling
function createSongCard(song) {
    return `
        <div class="music-card" data-id="${song.id}">
            <div class="music-thumbnail">
                <img src="${song.thumbnail}" alt="${song.title}">
                <button class="play-song-btn" onclick="handlePlayback(this.closest('.music-card').querySelector('audio'), this.querySelector('i'))">
                    <i class="fas fa-play"></i>
                </button>
            </div>
            <div class="music-info">
                <h3>${song.title}</h3>
                <p>${song.artist}  ${song.views} views</p>
                <audio controls>
                    <source src="${song.audioUrl}" type="audio/mp3">
                </audio>
            </div>
        </div>
    `;
}

function loadSongs() {
    const trendingGrid = document.getElementById('trendingGrid');
    const newReleasesGrid = document.getElementById('newReleasesGrid');
    const allSongsGrid = document.getElementById('allSongsGrid');

    const trendingSongs = songs.filter(song => song.category === 'trending');
    const newSongs = songs.filter(song => song.category === 'new');
    const happySongs = songs.filter(song => song.category === 'happy');
    if (trendingGrid) {
        trendingGrid.innerHTML = trendingSongs.map(createSongCard).join('');
    }

    if (newReleasesGrid) {
        newReleasesGrid.innerHTML = newSongs.map(createSongCard).join('');
    }

    if (allSongsGrid) {
        allSongsGrid.innerHTML = songs.map(createSongCard).join('');
    }
}

// Search functionality
const searchInput = document.querySelector('.search-bar input');
searchInput.addEventListener('input', (e) => {
    const searchTerm = e.target.value.toLowerCase();
    const filteredSongs = songs.filter(song => 
        song.title.toLowerCase().includes(searchTerm) || 
        song.artist.toLowerCase().includes(searchTerm)
    );
    
    document.getElementById('allSongsGrid').innerHTML = 
        filteredSongs.map(createSongCard).join('');
});

// Filter functionality
document.querySelectorAll('.filter-btn').forEach(btn => {
    btn.addEventListener('click', () => {
        document.querySelectorAll('.filter-btn').forEach(b => 
            b.classList.remove('active'));
        btn.classList.add('active');
        
        const filter = btn.dataset.filter;
        const categories = document.querySelectorAll('.category');
        
        categories.forEach(category => {
            if (filter === 'all') {
                category.style.display = 'block';
            } else {
                category.style.display = 
                    category.querySelector('h3').textContent.toLowerCase().includes(filter) 
                    ? 'block' : 'none';
            }
        });
    });
});

// Initialize
document.addEventListener('DOMContentLoaded', loadSongs);

